import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { createStore } from "redux";  

import reducer from "./reducers";
import Store from "./store";
import './App.scss';
import {Card} from './components/Card/Card';

const useFetch = () => {
  const [data, updateData] = useState(null);  
  const requestUrl = 'https://cat-fact.herokuapp.com/facts/';
  useEffect( () => {
    const fetchData = async () => {
      const response = await axios.get(requestUrl);
      updateData(response.data);
    }
    fetchData();
  },[])
  return data;
 }

const App = () =>{
  const cards = useFetch();
  const store = Store(reducer, cards);

  if(store.getState() != null){
    const [likedData, updateLikedData] = useState(null); 
    return(
      <div className="cardContainer">
        <button onClick={()=>{updateLikedData(1)}}></button>
        {
          Object.values(store.getState()).map((item, index) => {
            return(
            <Card 
            key={index}
            idCard={index}
            text={item.text}
            updatedAt={item.updatedAt}
            like={item.liked}
            store={store}
            />
            )
          })
        }
      </div>
  )
  } else {
    return(
      <div></div>
    )
  }
  }



export default App;
