import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { createStore } from "redux";  

import reducer from "./reducers";
import Store from "./store";
import './App.scss';
import {Card} from './components/Card/Card';

const useFetch = () => {
  const [data, updateData] = useState(null);  
  const requestUrl = 'https://cat-fact.herokuapp.com/facts/';
  useEffect( () => {
    const fetchData = async () => {
      const response = await axios.get(requestUrl);
      updateData(response.data);
    }
    fetchData();
  },[])
  return data;
 }

const App = () =>{
  const cards = useFetch();
  const store = Store(reducer, cards);
  //const [likedData, updateLikedData] = useState(null); 
  let likeData = 0; 

  if(store.getState() != null){
    return(
      <div className="cardContainer">
        <button onClick={()=>{updateLikedData(likeData)}}>Only liked</button>
        {
          Object.values(store.getState()).map((item, index) => {
            if (likeData == null && item.liked){
              return(
                <Card 
                key={index}
                idCard={index}
                text={item.text}
                updatedAt={item.updatedAt}
                like={item.liked}
                store={store}
                />
                )
            } 
            else {
              return(
                <Card 
                key={index}
                idCard={index}
                text={item.text}
                updatedAt={item.updatedAt}
                like={item.liked}
                store={store}
                />
                )
            }
          })
        }
      </div>
  )
  } else {
    return(
      <div></div>
    )
  }
}

const updateLikedData = (likeData) =>{
  console.log(likeData);
  likeData == 0 ? likeData = 1 : likeData = 0;
  console.log(likeData);

}



export default App;
