import React, {useContext} from 'react';

import './Card.scss';
import {RateContext} from '../../context/Context';

export const Card = (props) => {
    const {state} = useContext(RateContext);
    return(
        <div className={props.like == true ? "card like" : "card"}>
            <div className="cardText">{props.text}</div>
            <div className="cardDate">{props.date}</div>
            <div onClick={e => likeHandler(e, state)} data-index={props.idCard}>Кликнуть</div>
        </div>
    )
}

const likeHandler = (e, state) => {
    const idLike = e.target.dataset.index;
    Object.values(state.cards).map((item, index) => {
        if(idLike == index){
            console.log(state.cards);
        }
    })
  }
