import React, {useContext} from 'react';

import './Card.scss';
import { RateContext } from '../context/Context';

export const Card = (props) => {
    const {state} = useContext(RateContext);
    return(
        <div className={props.like == true ? "card like" : "card"}>
            <div className="cardText">{props.text}</div>
            <div className="cardDate">{props.date}</div>
            <button onClick={()=>{props.like = false}}>Like</button>
        </div>
    )
}
