import React, {useContext, useState} from 'react';

import './Card.scss';
import {RateContext} from '../../context/Context';

export const Card = (props) => {
    const {state} = useContext(RateContext);
    return(
        <div className={props.like == true ? "card like" : "card"} data-index={props.idCard}>
            <div className="cardText">{props.text}</div>
            <div className="cardDate">{props.date}</div>
            <div onClick={e => likeHandler(e, state)} data-index={props.idCard}>Like</div>
            <div onClick={e => deleteHandler(e, state)} data-index={props.idCard}>Delete</div>
        </div>
    )
}

