import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { createStore } from "redux";  

import reducer from "./reducers";
import Store from "./store";
import './App.scss';
import {Card} from './components/Card/Card';


const useFetch = () => {
  const [data, updateData] = useState(null);  
  const requestUrl = 'https://cat-fact.herokuapp.com/facts/';
  useEffect( () => {
    const fetchData = async () => {
      const response = await axios.get(requestUrl);
      updateData(response.data);
    }
    fetchData();
  },[])
  return data;
 }

 let likeData = 0;

const App = () =>{
  const cards = useFetch();
  const store = Store(reducer, cards);
  const [likedData, updateLikedData] = useState(0); 

  if(store.getState() != null){
    return(
      <div className="cardContainer">
        <button onClick={()=>{likedData == 0 ? updateLikedData(1) : updateLikedData(0)}}>Only liked</button>
        {
          Object.values(store.getState()).map((item, index) => {
            if (likedData == 1 && item.liked == true){
              return(
                <Card 
                key={index}
                idCard={index}
                text={item.text}
                updatedAt={item.updatedAt}
                like={item.liked}
                store={store}
                />
                )
            } 
            else {
              return(
                <div key={index}></div>
                // <Card 
                // key={index}
                // idCard={index}
                // text={item.text}
                // updatedAt={item.updatedAt}
                // like={item.liked}
                // store={store}
                // />
                )
            }
          })
        }
      </div>
  )
  } else {
    return(
      <div></div>
    )
  }
}

const updateLikedData = () =>{
  console.log(likeData);
  likeData == 0 ? likeData = 1 : likeData = 0;
  console.log(likeData);
}



export default App;
